import React from 'react';
import 'antd/dist/antd.css';
import axios from 'axios';
import moment from 'moment';
// import swal from 'sweetalert';
import {
  Form,
  Input,
  Button,
  DatePicker,
  Radio,
  Switch,
  Card,
  Icon,
  Select,
  message,
  Row,
  Col,
  Table
} from 'antd';

const { TextArea } = Input;
const { Option } = Select;

class RequestLeave extends React.Component {
  
  componentWillMount() {
    this.setState({
      employees : [],
      types : [],
      count : [],
      summery : []
    })

    axios.get('http://localhost:5000/leave_count/summery/'+4, 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Summery", res.data);
        this.setState({
            summery : res.data
        })
    }) 

    axios.get('http://localhost:5000/employee/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Employees:  ", res.data);
        this.setState({
            employees : res.data,
            duty : res.data[0].emp_id
        })
    })

    axios.get('http://localhost:5000/leave_type/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Leave Types:  ", res.data);
        this.setState({
            types : res.data
        })
    })

    axios.get('http://localhost:5000/leave_count/profile/'+4, 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Leave Count", res.data);
        this.setState({
            count : res.data
        })
    }) 
  }

  state = {
    types : [],
    employees : [],
    summery : [],
    period : null,
    disable : false,
    startValue: null,
    endValue: null,
    endOpen: false,
    count: [],
    type: null,
    error: null,
    duty: null,
    start_half: "Full Day",
    end_half: "Full Day",
    start_full_disable: false,
    start_morning_disable: true,
    half_day: false
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      
      if (!err && this.state.error==null) {

        const leave = {
          leave_type : { leave_type_id : values.leave_type } || undefined,
          employee : { emp_id : 4 } || undefined,
          start_date : this.state.startValue || undefined,
          end_date : this.state.endValue || this.state.startValue,
          startHalf : this.state.start_half,
          endHalf : this.state.end_half,
          number_of_leave_days : parseFloat(values.days) || undefined,
          duty : {emp_id : this.state.duty}, //{ emp_id : parseInt(values.duty) },
          special_notes : values.note || "No special note.",
          status : "Pending",
          reject : "Not reject"
        }
        
        console.log('Received values of form: ', leave);

        axios.post(
          'http://localhost:5000/leave/request', 
          leave, 
          { 
              headers: {
                  Authorization: 'Bearer ' + localStorage.getItem("header")
              }
          }
        )
        .then(res => {
          console.log("res", res.data);
          message.success(res.data); 
          this.props.form.resetFields();
          this.setState({
            disable: false,
          });
          // window.location.reload();
        })
        .catch(e => {
          console.log("Error", e.response);
          message.error(e.response.data.error);
          // swal("Oops!","Something Went Wrong !!!","error"); 
        })
      }else if(this.state.error !== null){
        message.error("Leave limit exceeded!");
      }
    });
  };

  handleCancel = () => {
    this.props.form.resetFields();
    this.setState({
      disable: false,
      period: null,
      start_full_disable : false,
      start_morning_disable : true,
      start_half : "Full Day",
      end_half : "Full Day",
      error: null
    });
  };

  disabledStartDate = current => {
    // const { endValue } = this.state;
    // if (!startValue || !endValue) {
    //   return false;
    // }
    // return startValue.valueOf() > endValue.valueOf();
    return current && current < moment().endOf('day');
  };

  disabledEndDate = current => {
    // const { startValue } = this.state;
    // if (!endValue || !startValue) {
    //   return false;
    // }
    // return endValue.valueOf() <= startValue.valueOf();
    return current && current < moment().endOf('day');
  };

  onChange = (field, value) => {
    this.setState({
      [field]: value,
    });
  };

  onStartChange = (date, dateString) => {
    this.onChange('startValue', dateString);
    console.log(date)
    console.log(dateString)
  };

  onEndChange = (date, dateString) => {
    var e = new Date(date)
    var s = new Date(this.state.startValue)
    var dif = parseInt(e-s)/(1000*60*60*24)
    this.checkCount(parseInt(dif)+1);
    this.onChange('endValue', dateString);
    this.onChange('period', parseInt(dif)+1);
    this.onChange('start_half', "Full Day");
    this.onChange('end_half', "Full Day");
  };

  setType = (e) => {
    this.handleCancel();
    this.onChange('type', this.state.types[e.target.value-1].type);
  }

  checkCount = (period) => {
    if(this.state.type === 'Casual' && period > (this.state.types[0].count-this.state.count.casual)){
      this.onChange('error', "* Exceed your casual limit");
    }else if(this.state.type === 'Medical' && period > (this.state.types[1].count-this.state.count.medical)){
      this.onChange('error', "* Exceed your medical limit");
    }else if(this.state.type === 'Maternity' && period > (this.state.types[2].count-this.state.count.maternity)){
      this.onChange('error', "* Exceed your maternity limit");
    }else if(this.state.type === 'Paternity' && period > (this.state.types[3].count-this.state.count.paternity)){
      this.onChange('error', "* Exceed your paternity limit");
    }else if(this.state.type === 'Annual' && period > (this.state.types[4].count-this.state.count.annual)){
      this.onChange('error', "* Exceed your annual limit");  
    }else this.onChange('error', null);  
  }

  handleStartChange = open => {
    if (!open && !this.state.disable) {
      this.setState({ endOpen: true });
    }
  };

  handleEndChange = open => {
    this.setState({ endOpen: open });
  };

  halfDay = (value) => {
    this.onChange('half_day', value);
    if(value){
      this.setState({
        period : 0.5,
        disable : true,
        endValue : this.state.startValue,
        start_full_disable : true,
        start_morning_disable : false,
        start_half : "Morning"
      })
      this.checkCount(0.5);
    }else{
      this.setState({
        period : null,
        disable : false,
        error : null,
        start_full_disable : false,
        start_morning_disable : true,
        start_half : "Full Day"
      })
    }  
  } 

  handleDuty = (emp) => {
    this.setState({
      duty: emp
    })
    console.log(emp)
  } 

  startHalfChange = (e) => {
    this.setState({
      start_half: e.target.value
    })
    if(e.target.value === "Evening" && !this.state.half_day && this.state.period!==null){
      this.onChange('period', this.state.period-0.5);
    } else if(e.target.value === "Full Day" && this.state.period!==null){
      this.onChange('period', this.state.period+0.5);
    }
  }

  endHalfChange = (e) => {
    this.setState({
      end_half: e.target.value
    })
    if(e.target.value === "Morning" && this.state.period!==null){
      this.onChange('period', this.state.period-0.5);
    } else if(e.target.value === "Full Day" && this.state.period!==null){
      this.onChange('period', this.state.period+0.5);
    }
  }

  render() {
    const { getFieldDecorator } = this.props.form;
    const { endOpen, summery } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    const columns = [
      {
        title: 'Type',
        key: '0',
        dataIndex: 'type'
      },
      {
        title: 'Entitlement',
        key: '1',
        dataIndex: 'entitlement'
      },    
      {
        title: 'Utilized',
        key: '2',
        dataIndex: 'utilized'
      },
      {
        title: 'Remaining',
        key: '3',
        dataIndex: 'remaining'
      }
    ];

    return (
        <div>
          <Row gutter={16}>
            <Col span={14} > 
              <Card title='Leave Request Form' bordered={false} hoverable='true'>  
                <Form {...formItemLayout} onSubmit={this.handleSubmit}>

                <Form.Item label="Leave Type">
                  {getFieldDecorator('leave_type', {
                    rules: [{ required: true, message: 'Please input leave type!' }],
                  })(
                  <Radio.Group name="radiogroup" style={{ width: '450px' }} onChange={this.setType}>
                  {this.state.types.map(item => (
                          <Radio.Button key={item.leave_type_id} value={item.leave_type_id}>{item.type}</Radio.Button>
                  ))}   
                  </Radio.Group>
                )}
                </Form.Item>  
              
                <Form.Item label="Half Day">
                  {getFieldDecorator('halfDay', {
                    valuePropName: 'checked'
                  })(
                  <Switch
                    checkedChildren={<Icon type="check" />}
                    unCheckedChildren={<Icon type="close" />}
                    onChange= {this.halfDay} 
                  />
                  )}
                </Form.Item>

                <Form.Item label="Start date">
                {getFieldDecorator('start_date', {
                    rules: [{ required: true, message: 'Please input Start Date!' }],
                  })(
                  <DatePicker
                    disabledDate={this.disabledStartDate}
                    format="YYYY-MM-DD"
                    placeholder="Pickup a Date"
                    onChange={this.onStartChange}
                    onOpenChange={this.handleStartChange}
                    style={{ width: '280px' }}
                  />
                  )}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <Radio.Group onChange={this.startHalfChange} value={this.state.start_half}>
                    <Radio value="Full Day" disabled={this.state.start_full_disable}>Full Day</Radio>
                    <Radio value="Morning" disabled={this.state.start_morning_disable}>Morning</Radio>
                    <Radio value="Evening">Evening</Radio>
                  </Radio.Group>
                </Form.Item>

                {this.state.disable ? null :
                <Form.Item label="End date">
                  {getFieldDecorator('end_date')(
                  <DatePicker
                    disabledDate={this.disabledEndDate}
                    format="YYYY-MM-DD"
                    placeholder="Pickup a Date"
                    onChange={this.onEndChange}
                    open={endOpen}
                    onOpenChange={this.handleEndChange}
                    style={{ width: '280px' }}
                  />
                  )}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                  <Radio.Group onChange={this.endHalfChange} value={this.state.end_half}>
                    <Radio value="Full Day">Full Day</Radio>
                    <Radio value="Morning">Morning</Radio>
                  </Radio.Group>
                </Form.Item>
                }

                <Form.Item label="Number of leave days">
                  {getFieldDecorator('days', {
                    rules : [{ 
                      required: true, 
                      message: 'Please input End Date to get count!' 
                    }],
                    initialValue : this.state.period
                  })(<Input style={{width: '50px'}} disabled={true}/>)}
                  <span style={{color:'red'}}>&nbsp;&nbsp; {this.state.error}</span>
                </Form.Item>
                {this.state.employees.length!==0?  
                <Form.Item label="Duty Cover By">
                  <Select placeholder="Please select a employee" style={{ width: '450px' }} onChange={this.handleDuty} defaultValue={this.state.employees[0].first_name}>
                    {this.state.employees.map(item => (
                          <Option key={item.emp_id}>{item.first_name} {item.second_name}</Option>
                    ))}
                  </Select>
                </Form.Item>
                : null }      
                <Form.Item label="Special Notes">
                  {getFieldDecorator('note', {
                    rules: [{
                        message: 'Please input your note',
                      }],
                  })(<TextArea rows={4} style={{ width: '450px' }}/>)}
                </Form.Item>

                <Form.Item {...tailFormItemLayout}>
                  <Button type="primary" htmlType="submit" style={{width:'100px'}}>
                  <Icon type="check-circle" /> 
                    Submit
                  </Button>
                  &nbsp;&nbsp;&nbsp;&nbsp;
                  <Button type="danger" onClick={this.handleCancel} style={{width:'100px'}}>
                  <Icon type="close-circle" /> 
                    Cancel
                  </Button>
                </Form.Item>
              </Form>
              </Card>
            </Col>
            <Col span={10}>
              <Card>
              <Table columns={columns} dataSource={summery} size="small" pagination={false}/>
              </Card>
            </Col>
          </Row>
        
    </div>
    );
  }
}

const WrappedRequestLeave = Form.create({ name: 'register' })(RequestLeave);

export default WrappedRequestLeave;
