import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import 'antd/dist/antd.css';
import './index.css';
import { Form, Icon, Input, Button, Checkbox, Card, Row, Col, message } from 'antd';

class NormalLoginForm extends React.Component {
  
  state = {
    redirectToReferrer: true,
    user_id: null,
    user_name: null,
    user_role: null
  }

  handleLogin = e => {
    console.log("Props", this.props);
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      const user = {
        usernameOrEmail : values.username,
	      password : values.password
      }
      if (!err) {
        console.log('Received values of form: ', user);
        axios.post('http://localhost:5000/api/auth/signin', user)
        .then(token => {
          axios.get('http://localhost:5000/api/auth/info',
          {
            headers: {
              Authorization: 'Bearer ' + token.data.accessToken
            }
          })
          .then(res => {

            // this.setState({
            //   user_id: res.data.id,
            //   user_name: res.data.firstName+" "+res.data.secondName,
            //   user_role: res.data.role
            // })
            let user_id = res.data.id
            this.props.children(user_id);
            localStorage.setItem("user_id", res.data.id);
            localStorage.setItem("user_name", res.data.firstName+" "+res.data.secondName);
            localStorage.setItem("user_role", res.data.role);

          }).catch(e => {
            console.log(e);
          });

          localStorage.setItem("header", token.data.accessToken);
          this.props.history.push('/');

        }).catch(e => {

          message.error(e.response.data.error);
          this.props.form.resetFields();
          // window.location.reload();

        });
      }
    });
  };

  render() {
    // const {redirectToReferrer} = this.state
    // if (redirectToReferrer) {
    //   return (<Sidebar/>)
    // }
      
    const { getFieldDecorator } = this.props.form;
    
    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 20 },
      },
    };

    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 16,
          offset: 0,
        },
        sm: {
          span: 24,
          offset: 0,
        },
      },
    };

    return (
      <div style={{ background: '#ECECEC', padding: '150px', height: '760px' }}>
        <Row gutter={5}>
          <Col span={10} offset={4}>
          <img height= '400px' alt="example" src="https://www.a1vacationhomes.com/pvrdesign/sample/img/hero/beach-vacation-rentals.jpg" />
          </Col> 
          <Col span={9}>
          <Card title="VX Leave" bordered={false} style={{ width: 300, height: '400px'}}>
        <center>
        <Form  {...formItemLayout} className="login-form">
          <br/>
          <Form.Item>
            {getFieldDecorator('username', {
              rules: [{ required: true, message: 'Please input your username!' }],
            })(
              <Input
                style={{width: '250px'}}
                prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                placeholder="Username"
              />,
            )}
          </Form.Item>
          <Form.Item>
            {getFieldDecorator('password', {
              rules: [{ required: true, message: 'Please input your Password!' }],
            })(
              <Input.Password
                style={{width: '250px'}}
                prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                type="password"
                placeholder="Password"
              />,
            )}
          </Form.Item>
          <br/><br/>
          <Form.Item {...tailFormItemLayout}>
          {getFieldDecorator('remember', {
              valuePropName: 'checked',
              initialValue: false,
            })(<Checkbox className='box'>Remember me</Checkbox>)}
            <Link className="login-form-forgot" to='/signup'>Forgot Password</Link>
            <br/>
            <Button onClick={this.handleLogin} type="primary" htmlType="submit" className="login-form-button">
              Log in
            </Button>
          </Form.Item>
        </Form>
        </center>
        </Card>
          </Col> 
        </Row>
  </div>
    );
  }
}

const WrappedNormalLoginForm = Form.create({ name: 'normal_login' })(NormalLoginForm);

export default WrappedNormalLoginForm
