import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Drawer, Card, Modal } from 'antd';
import Highlighter from 'react-highlight-words';
import swal from 'sweetalert';
import UpdateEmployee from '../Employee/UpdateEmployee';
import AddEmployee from '../Employee/AddEmployee';

function info(leave) {
  Modal.info({
    title: 'Leave Record of '+ `${leave.employee.first_name}`,
    content: (
      <div>
        <p>some messages...some messages...</p>
        <p>some messages...some messages...</p>
      </div>
    ),
    onOk() {},
  });
}

class ReadLeaves extends Component {
    
    componentWillMount(){
        this.setState({
            data : []
        })

        axios.get('http://localhost:5000/leave/all', 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : (res.data).reverse()
            })
        }) 
    } 

    state = {
        searchText: '',
        data : [] 
    };
    
      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   

    handleInfo = (emp) => {
      swal(
        `
        Id: ${emp.emp_id} 
        Name: ${emp.first_name} ${emp.second_name}
        Initials: ${emp.initials}
        Email: ${emp.email}
        Contact: 0${emp.contact}
        Address: ${emp.residence}
        Supervisor 01: ${emp.supervisor1}
        Supervisor 02: ${emp.supervisor2}
        Join Date: ${emp.join_date}
        Confirm Date: ${emp.confirm_date}
         `,
      );
      console.log(AddEmployee.state)
    }

    handleDelete(leave,e) {
        e.preventDefault();
        console.log("Delete record: ", leave.leave_id)
        swal({
            text: "Wanna delete that request?",
            buttons: {
                confirm: "Delete",
                cancel: true,
            }
        })
        .then(willSearch => {
            if (willSearch) {
                axios.get('http://localhost:5000/leave/delete/'+leave.leave_id, 
                {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem("header")
                    }
                })
                .then(res => {
                    swal("Yeah!",res.data,"success"); 
                    window.location.reload(); 
                }).catch( err => {
                    console.log("eeeeeeeeeeeeee",err)
                    swal("Oops!","Something Went Wrong!!!","error");
                })
            }
        })
    }
    
    render() {
       
        const columns = [
            {
              title: 'Name',
              dataIndex: 'employee',
              render: emp => (
               emp.first_name
              ),
              // ...this.getColumnSearchProps('employee'),
            },  
            {
              title: 'Leave Type',
              dataIndex: 'leave_type',
              render: type => (
               type.type
              )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
            },
            {
                title: 'Status',
                dataIndex: 'status',
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Button type="default" onClick={info.bind(this, emp)} shape="circle" icon="info" />,
            },
            {
                dataIndex: '',
                key: 'y',
                width: '3%',
                render: (emp) => <Button type="danger" onClick={this.handleDelete.bind(this, emp)} shape="circle" icon="delete" />,
            },

          ];

        return (
            <div>
              <Card title="Pending Leaves">
                <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />

                <Drawer
                title="Update Existing Employee"
                width={570}
                onClose={this.onUpdateClose}
                visible={this.state.visibleUpdate}
                >
                    <UpdateEmployee update={this.state.updateEmployee} close={this.onUpdateClose} />
                </Drawer>

                <Drawer
                title="Add New Employee"
                width={570}
                onClose={this.onAddClose}
                visible={this.state.visibleAdd}
                >
                    <AddEmployee close={this.onAddClose}/>
                </Drawer>
                </Card> 
            </div>
        )
    }
}    


export default ReadLeaves;
