import React from 'react';
import 'antd/dist/antd.css';
import axios from 'axios';
import { Calendar, Alert, Card, Modal, Form, Input, message } from 'antd';
import moment from 'moment';

const ReasonForm = Form.create({ name: 'form_in_modal' })(

  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Set holiday"
          okText="Save"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical">
            <Form.Item label="Reason">
              {getFieldDecorator('reason', {
                rules: [{ required: true, message: 'Please input the reason' }],
              })(<Input type="textarea"/>)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

class LeaveCalender extends React.Component {
  
  state = {
    value: moment('2020-01-25'),
    selectedValue: moment('2020-01-25'),
    visible: false
  };

  onSelect = value => {
    this.setState({
      value,
      selectedValue: value,
      visible: true
    });
  };

  onPanelChange = value => {
    this.setState({ value });
  };

  handleCancel = () => {
    this.setState({ 
      visible: false
    });
  };

  handleSet = () => {
    
    this.props.form.validateFieldsAndScroll((err, values) => {
      console.log(values.reason);
      console.log(this.state.value);
      // const calendar = {
      //   date: this.state.email,  
      //   reason : values.code
      // }

      // if (!err) {
      //   axios.post('http://localhost:5000/api/auth/forgot_password', 
      //   calendar,
      //   {
      //       headers: {
      //           Authorization: 'Bearer ' + localStorage.getItem("header")
      //       }
      //   })
      //   .then(res => {
      //       if(res.data === true){
      //         message.success("Password Updated Successfully!");
      //         this.props.form.resetFields(); 
      //       }  else{
      //         message.error("Confirm code not match!"); 
      //       }
      //   }).catch(e => {
      //     console.log(e);
      //     message.error("Something went wrong!"); 
      //   })
        
      // }
    });
  }

  render() {
    const { value, selectedValue } = this.state;
    return (
      <div>
        <Alert
          message={`You selected date: ${selectedValue && selectedValue.format('YYYY-MM-DD')}`}
        />
        <br/>
        <Card>
          <Calendar value={value} onSelect={this.onSelect} onPanelChange={this.onPanelChange} />
        </Card>

        <ReasonForm
          wrappedComponentRef={this.saveFormRef}
          visible={this.state.visible}
          onCancel={this.handleCancel}
          onCreate={this.handleSet}
        />
      </div>
    );
  }
}


const WrappedLeaveCalender = Form.create({ name: 'update' })(LeaveCalender);

export default WrappedLeaveCalender;
