import React from "react";
import { Icon, Form, Button, Input, Card, message } from 'antd';
import axios from 'axios';

class ChangePassword extends React.Component {

    parseJwt (token) {
      var base64Url = token.split('.')[1];
      var base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      var jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));
  
      return JSON.parse(jsonPayload).sub;
    };

    state = {
        confirmDirty: false
    }

    changePassword = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
          const passwords = {
            currentPassword : values.currentPassword,
            newPassword : values.newPassword
          }

          if (!err) {
            console.log("Received values of form: ", values);
            axios.post('http://localhost:5000/api/auth/get_old', 
            passwords,
            {
                headers: {
                    Authorization: 'Bearer ' + localStorage.getItem("header")
                }
            })
            .then(res => {
                console.log("res", res.data);
                if(res.data === true){
                  message.error("Not match to current!"); 
                }  else{
                  message.error("Not match to current!");
                }
            }).catch(e => {
              console.log(e);
              message.error("Not match to current!"); 
            })
            
          }
        });
    }
    
    handelCancel() {
      this.props.form.resetFields();
    }

    handleConfirmBlur = e => {
        const { value } = e.target;
        this.setState({ confirmDirty: this.state.confirmDirty || !!value });
    };
    
    compareToFirstPassword = (rule, value, callback) => {
        const { form } = this.props;
        if (value && value !== form.getFieldValue("newpassword")) {
          callback("Two passwords that you enter is inconsistent!");
        } else {
          callback();
        }
    };
    
    validateToNextPassword = (rule, value, callback) => {
        const { form } = this.props;
        if (value && this.state.confirmDirty) {
          form.validateFields(["confirm"], { force: true });
        }
        callback();
    };

    render () {

        const { getFieldDecorator } = this.props.form;
       
        const formItemLayout = {
            labelCol: {
              xs: { span: 24 },
              sm: { span: 8 },
            },
            wrapperCol: {
              xs: { span: 24 },
              sm: { span: 16 },
            },
        };

        const tailFormItemLayout = {
            wrapperCol: {
              xs: {
                span: 24,
                offset: 0,
              },
              sm: {
                span: 16,
                offset: 8,
              },
            },
        };

        return(
            <div>
                <Card title='Change Password' bordered={false} hoverable='true'> 
                 <Form {...formItemLayout} layout="vertical" hideRequiredMark>
            
                    <Form.Item hasFeedback>
                        {getFieldDecorator("currentPassword", {
                        rules: [
                            {
                            required: true,
                            message: "Please input your current password!"
                            },
                            {
                            validator: this.validateToNextPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '1'
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="Current Password"
                        onChange = {this.requestOld()}
                        />)}
                    </Form.Item>
                    <Form.Item hasFeedback>
                        {getFieldDecorator("newPassword", {
                        rules: [
                            {
                            required: true,
                            message: "Please input your new password!"
                            },
                            {
                            validator: this.validateToNextPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '2'
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="New Password"
                        />)}
                    </Form.Item>
                    <Form.Item hasFeedback>
                        {getFieldDecorator("confirm", {
                        rules: [
                            {
                            required: true,
                            message: "Please confirm your new password!"
                            },
                            {
                            validator: this.compareToFirstPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '3'
                        onBlur={this.handleConfirmBlur}
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="Confirm Password"
                        />)}
                    </Form.Item>
                    <Form.Item {...tailFormItemLayout}>
                        <Button type="primary" onClick={this.changePassword} style={{width:'100px'}}>
                        <Icon type="check-circle" /> 
                            Submit
                        </Button>
                        &nbsp;&nbsp;&nbsp;&nbsp;
                        <Button type="danger" onClick={this.handelCancel} style={{width:'100px'}}>
                        <Icon type="close-circle" /> 
                            Cancel
                        </Button>
                    </Form.Item>
                </Form>
                </Card>
            </div>
        );
    }
}

const WrappedChangePassword= Form.create({ name: 'register' })(ChangePassword);

export default WrappedChangePassword;
