import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Drawer, Card, Modal, Tag, message } from 'antd';
import Highlighter from 'react-highlight-words';
import UpdateEmployee from '../Employee/UpdateEmployee';
import AddEmployee from '../Employee/AddEmployee';

const { confirm } = Modal;

class ReadLeaves extends Component {
    
    componentWillMount(){
       this.reload();
    } 

    state = {
        searchText: '',
        data : [],
        visible: false,
        leave : [] 
    };
    
    reload () {
      this.setState({
        data : []
      })
    
      axios.get('http://localhost:5000/leave/all', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          console.log("res", res.data);
          this.setState({
              data : (res.data).reverse()
          })
      }) 

    }

      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   

    

    showModal = (leave) => {
      this.setState({
        visible: true,
        leave: leave
      });
    };
  
    handleOk = e => {
      console.log(e);
      this.setState({
        visible: false,
      });
    };

    showConfirm = (leave) =>{
      confirm({
        title: 'Do you want to remove this request ?',
        content: 'Make sure you can\'t delete pending leave request.',
        okText: 'Remove',
        okType: 'danger',
        onOk() {
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500);
            console.log(le)
            axios.get('http://localhost:5000/leave/delete/'+leave.leave_id, 
                    {
                        headers: {
                            Authorization: 'Bearer ' + localStorage.getItem("header")
                        }
                    })
                    .then(res => {
                        if(res.data === "pending" ){
                          message.error("Can't remove pending request.");
                        }else{
                          message.success("Removed request.");
                          this.reload();
                        }
                        
                    }).catch( err => {
                        message.error("Something Went Wrong!");
                    })
    
          }).catch(() => message.error("Something Went Wrong!"));
        },
        onCancel() {},
      });
      // setTimeout(() => {
      //   this.reload();
      // }, 5000);
    }

    render() {
       
        const {leave} = this.state;

        const columns = [
            {
              title: 'Name',
              dataIndex: 'user',
              ...this.getColumnSearchProps('user')
            },  
            {
              title: 'Leave Type',
              dataIndex: 'type',
              // render: type => (
              //  type.type
              // ),
              ...this.getColumnSearchProps('type')
            },
            {
                title: 'Start Date',
                dataIndex: 'startDate'
            },
            {
                title: 'End Date',
                dataIndex: 'endDate'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
                ...this.getColumnSearchProps('formatDateTime'),
            },
            {
              title: 'Status',
              dataIndex: 'status',
              render: tag => {
                        let color = tag.length > 5 ? 'geekblue' : 'green';
                        if (tag === 'Pending') {
                          color = 'green';
                        }else if (tag === 'Approved') {
                          color = 'geekblue';
                        }else {
                          color = 'red';
                        }
                        return (
                          <Tag color={color} key={tag}>
                            {tag}
                          </Tag>
                        );
                      },
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={this.showModal.bind(this, emp)}/>,
            },
            {
                dataIndex: '',
                key: 'y',
                width: '3%',
                render: (emp) => <Icon type="delete" onClick={this.showConfirm.bind(this, emp)}  theme="twoTone" twoToneColor='#EE204D'/>,
            },

          ];

        return (
            <div>
              <Card title="Leave Requests" hoverable='true'>
                <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />

                <Drawer
                title="Update Existing Employee"
                width={570}
                onClose={this.onUpdateClose}
                visible={this.state.visibleUpdate}
                >
                    <UpdateEmployee update={this.state.updateEmployee} close={this.onUpdateClose} />
                </Drawer>

                <Drawer
                title="Add New Employee"
                width={570}
                onClose={this.onAddClose}
                visible={this.state.visibleAdd}
                >
                    <AddEmployee close={this.onAddClose}/>
                </Drawer>
                </Card> 

                {leave.length!==0? <Modal
                  title="Full Leave Record"
                  visible={this.state.visible}
                  onOk={this.handleOk}
                  onCancel={this.handleOk}
                  footer={null}
                >
                  <p>Date of Request: <Tag color="volcano">{leave.formatDateTime}</Tag> Leave Type: <Tag color="volcano">{leave.type}</Tag></p>
                  <p>Leave Period: <Tag color="volcano">{leave.startDate} {leave.startHalf }</Tag>To  <Tag color="volcano">{leave.endDate} {leave.endHalf}</Tag></p>
                  <p>Number of Days: <Tag color="volcano">{leave.number_of_leave_days}</Tag></p>
                  <p>Status: <Tag color="volcano">{leave.status}</Tag> </p>
                  <p>Duty cover by: <Tag color="volcano">{leave.duty} </Tag></p>
                  <p>Special note: <Tag color="volcano">{leave.specialNotes}</Tag></p>
                  <p>If reject, reason: <Tag color="volcano">{leave.reject}</Tag></p>
                </Modal>: null}
            </div>
        )
    }
}    


export default ReadLeaves;
