import React from 'react';
import 'antd/dist/antd.css';
import axios from '../../config/axios'
import moment from 'moment';
import {
  Form,
  Input,
  Button,
  DatePicker,
  Card,
  Icon,
  message,
  Row,
  Col,
  Table,
  Modal,
  Radio
} from 'antd';

const { TextArea } = Input;
const { confirm } = Modal; 

class CollectLieu extends React.Component {

    state = {
        lieuLeaves : [],  
        types : [],
        employees : [],
        summery : [],
        period : null,
        disable : false,
        startValue: null,
        endValue: null,
        endOpen: false,
        type: null,
        error: null,
        duty: null,
        start_half: "Full Day",
        end_half: "Full Day",
        start_full_disable: false,
        start_morning_disable: true,
        half_day: false,
        start_day : null,
        end_day : null,
        saveDates : [],
        count: 0
    };

  componentWillMount() {

    this.getLieuLeaves();
  }

  getLieuLeaves() {

    axios.get('lieu_leave/get_own', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {

        if (res.data.success === true) {  
          this.setState({
            lieuLeaves : res.data.list
          })
        } else {
          message.error(res.data.message);
        }
      })
      .catch(e => {
        message.error("Something went wrong");
        console.log(e.response.data.error);
      }) 

  }

    handleSubmit = e => {
        e.preventDefault();
        confirm({
        title: 'Sure to submit your lieu leave collecting request?',
        content: 'If you submit, Send request to administraters and supervisors.',
        okText: 'Sumbit',
        okType: 'primary',
            onOk: () => {
                this.props.form.validateFieldsAndScroll((err, values) => {

                    if (!err && this.state.error==null) {

                        const leave = {
                            date : values.date,
                            period : values.period,
                            project : values.project,
                            worksDone : values.worksDone
                        }

                        console.log("Lieu leave request --------------> ", leave);

                        axios.post(
                            'lieu_leave/save', 
                            leave, 
                            { 
                                headers: {
                                    Authorization: 'Bearer ' + localStorage.getItem("header")
                                }
                            }
                        )
                        .then(res => {
                            if (res.data.success === true) {              
                                message.success(res.data.message); 
                                this.getLieuLeaves();
                                this.handleCancel();
                            } else {
                                message.error(res.data.message);
                            }
                        })
                        .catch(e => {
                            console.log(e.response.data.error);
                            message.error("Something went wrong"); 
                        })

                    }else if(this.state.error !== null){
                        message.error(this.state.error);
                    }
                });
            }
        })
    }

  handleCancel = () => {
    this.props.form.resetFields();
    this.setState({
      disable : false,
      period : null,
      start_full_disable : false,
      start_morning_disable : true,
      start_half : "Full Day",
      end_half : "Full Day",
      error: null,
      count : 0
    });
  };

  disabledStartDate = startValue => {
    return startValue && startValue < moment().endOf('day');
  };

  onChange = (field, value) => {
    this.setState({
      [field]: value,
    });
  };

  onStartChange = (date, dateString) => {
    this.onChange('startValue', dateString);
    this.onChange('start_day',date)
  };

  handleStartChange = open => {
    if (!open && !this.state.disable) {
      this.setState({ endOpen: true });
    }
  };


  render() {
    const { getFieldDecorator } = this.props.form;
    const { summery } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };

    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    const columns = [
      {
        title: 'Date',
        key: '0',
        dataIndex: 'date'
      },
      {
        title: 'Period',
        key: '1',
        dataIndex: 'period'
      },    
      {
        title: 'Project',
        key: '2',
        dataIndex: 'project'
      },    
      {
        title: 'Status',
        key: '3',
        dataIndex: 'isApproved'
      }
    ];

    return (
        <div>
          <Row gutter={16}>
            <Col span={13} > 
              <Card title='Collect the lieu leaves' bordered={false} hoverable='true'>  
                <Form {...formItemLayout} onSubmit={this.handleSubmit}>

                <Form.Item label="Date">
                {getFieldDecorator('date', {
                    rules: [{ required: true, message: 'Please input Date!' }],
                  })(
                  <DatePicker
                    disabledDate={this.disabledStartDate}
                    format="YYYY-MM-DD"
                    placeholder="Pickup a Date"
                    onChange={this.onStartChange}
                    onOpenChange={this.handleStartChange}
                    style={{ width: '400px' }}
                  />
                  )}
                </Form.Item>

                <Form.Item label="Period">
                {getFieldDecorator('period', {
                    rules: [{ required: true, message: 'Please input worked period!' }],
                  })(
                    <Radio.Group  defaultValue={0}>
                        <Radio.Button value={0}>Half Day</Radio.Button>
                        <Radio.Button value={1}>Full Day</Radio.Button>
                    </Radio.Group>
                  )}
                </Form.Item>                

                <Form.Item label="Project">
                  {getFieldDecorator('project', {
                    rules : [{ 
                      required: true, 
                      message: 'Please input the project!' 
                    }],
                    initialValue : this.state.period
                  })(<Input style={{width: '400px'}} />)}
                </Form.Item>

                <Form.Item label="Works done">
                  {getFieldDecorator('worksDone', {
                    rules: [{
                        required: true, 
                        message: 'Please input the works you done',
                      }],
                  })(<TextArea rows={4} maxLength = {1000} style={{ width: '400px', height: '170px' }}/>)}
                </Form.Item>

                <Form.Item {...tailFormItemLayout}>
                  <Button type="primary" htmlType="submit" style={{width:'100px'}}>
                  <Icon type="check-circle" /> 
                    Submit
                  </Button>
                  &nbsp;&nbsp;&nbsp;&nbsp;
                  <Button type="danger" onClick={this.handleCancel} style={{width:'100px'}}>
                  <Icon type="close-circle" /> 
                    Cancel
                  </Button>
                </Form.Item>
              </Form>
              </Card>
            </Col>
            <Col span={11}>
              <Card hoverable='true'>
              <Table rowKey={record => record.type} columns={columns} dataSource={summery} size="small" pagination={false}/>
              </Card>
            </Col>
          </Row>

    </div>
    );
  }
}

const WrappedCollectLieu = Form.create({ name: 'register' })(CollectLieu);

export default WrappedCollectLieu;
