import React, { Component } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import 'antd/dist/antd.css';
import { Button, Form, Row, Col, Card, Progress, Modal } from 'antd';
import swal from 'sweetalert';

function info(leave) {
    Modal.info({
      title: 'Fully Leave Record',
      content: (
        <div>
         
        </div>
      ),
      onOk() {},
    });
  }

class Dashboard extends Component {
    
    componentWillMount(){
        this.setState({
            data : []
        })
        axios.get('http://localhost:5000/leave/find'+4, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : (res.data).reverse()
            })
        }) 
    } 

    state = {
        data : []
    }
   
    showDrawer = (id) => {
        this.setState({
          visible: true,
          id : id
        });
        axios.get('http://localhost:5000/search/'+id)
        .then(res => {
            this.setState({
                name : res.data.name,
                email: res.data.email,
                contact: res.data.contact,
                salary : res.data.salary
            })
            console.log("aaaaaaaa",res.data)
        }).catch( err => {
            console.log(err)
            swal("Oops!","Something Went Wrong!!!","error");
        }) 
      };
    
    onClose = () => {
        this.setState({
          visible: false
        });
    };

    handleClick(id) {
        axios.get('http://localhost:5000/favorites/'+id)
        .then(res => {
            console.log("res", res.data);
            this.setState({
                favorites : res.data,
                user_name : res.data[0].user_name
            })
            console.log(this.state.user_name);
        }).catch(e => {
            const data = this.state.data;
            for(let i=0; i<data.length;i++){
                if(this.state.data[i].user_id === id){
                    let user = this.state.data[i].name;
                    this.setState({
                        favorites : [],
                        user_name : user
                    })
                    swal("Oops!",  user+" hasn't any favorites!!!", "warning" );
                }
            }
        })
    }

    handleUpdate = (e) => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                const user = {
                    name: values.name || undefined,
                    email: values.email || undefined,
                    contact: values.contact || undefined,
                    salary: values.salary || undefined
                }

                if(user.name === this.state.name && user.password === this.state.password && user.salary === this.state.salary && user.email === this.state.email && user.contact === this.state.contact){
                    
                    swal("Ohhh!","Didn't change any field!!!", "warning");
                
                }else{

                    axios.put('http://localhost:5000/update/'+this.state.id, user)
                    .then(res => {
                        console.log("res", res.data);
                        swal("Yeah!","User is updated!!!","success");
                        this.onClose();
                        this.props.history.push('/dashboard');
                    }).catch(e=>{
                        console.log("error", e);
                        swal("Oops!","User not updated!!!","error");
                        this.onClose();
                    })
                }
                console.log("state", user);
            }
        });
    }

    handleDelete(id,e) {
        e.preventDefault();
        
        swal({
            text: "Wanna delete that user?",
            buttons: {
                confirm: "Delete",
                cancel: true,
            }
        })
        .then(willSearch => {
            if (willSearch) {
                axios.get('http://localhost:5000/delete/'+id)
                .then(res => {
                    swal("Yeah!",res.data,"success"); 
                    this.props.history.push('/dashboard'); 
                }).catch( err => {
                    swal("Oops!","Something Went Wrong!!!","error");
                })
            }
        })
    }

    render() {

        const columns = [
            {
              title: 'Name',
              dataIndex: 'employee',
              render: emp => (
               emp.first_name
              ),
              // ...this.getColumnSearchProps('employee'),
            },  
            {
              title: 'Leave Type',
              dataIndex: 'leave_type',
              render: type => (
               type.type
              )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
            },
            {
                title: 'Status',
                dataIndex: 'status',
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Button type="default" onClick={info.bind(this, emp)} shape="circle" icon="info" />,
            },
            {
                dataIndex: '',
                key: 'y',
                width: '3%',
                render: (emp) => <Button type="danger" onClick={this.handleDelete.bind(this, emp)} shape="circle" icon="delete" />,
            },

        ];
        return (
            <div className='center'>
                <Card>
                <Row gutter={16}>
                <Col span={8}>    
                <Progress type="circle" percent={75} format={percent => `${percent} Days`}/>
                <br/>
                <span>Annual Leave</span>
                </Col>
                <Col span={8}>
                <Progress type="circle" percent={50} />
                <br/>
                <span>Casual Leave</span>
                </Col>
                <Col span={8}>
                <Progress type="circle" percent={100} />
                <br/>
                <span>Medical Leave</span>
                </Col>
                </Row>
                </Card>
                <br/>

                <Card>
                    <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />
                </Card>
              
            </div>
        )
    }
}    

const Dash = Form.create({name: "dashboard"})(Dashboard);

export default Dash;
