import React, { Component } from 'react';
import './App.css';

class App extends Component {
    
    constructor() {
        super();
        this.state = {
            turn : 'X',
            winner : undefined,
            winnerLine: undefined,
            gameEnded : false,
            board : Array(9).fill(''),
            totalMoves : 0 
        }
    }
    clicked(e) {
        if(this.state.board[e.target.dataset.square] === ''){
            this.state.board[e.target.dataset.square] = this.state.turn;
            e.target.innerText = this.state.turn;
            let totalMoves=this.state.totalMoves;
            this.setState({
                turn: this.state.turn === 'X' ? 'O' : 'X',
                board: this.state.board,
                totalMoves: totalMoves+1
            })
        }
        var result = this.check();
        if(result === 'X'){
            this.setState({
                gameEnded : true,
                winner : 'X',
                winnerLine : "X won the match!!!"
            })
        }else if(result === 'O'){
            this.setState({
                gameEnded : true,
                winner : 'O',
                winnerLine : "O won the match!!!"
            })
        }else if(result === 'draw'){
            this.setState({
                gameEnded : true,
                winner : 'draw',
                winnerLine : "Match is drawn!!!"
            })
        }
        console.log(this.state.totalMoves)
    }

    check() {
        
        var moves = [[0,3,6],[1,4,7],[2,5,8],[0,4,8],[2,4,6],[0,1,2],[3,4,5],[6,7,8]];
        var board = this.state.board;
        for(let i=0; i<moves.length; i++){
            if(board[moves[i][0]]===board[moves[i][1]] && board[moves[i][1]] ===board[moves[i][2]]){
                return board[moves[i][0]];
            }
        }
        if(this.state.totalMoves === 8){
            return 'draw';
        }
    }

    handleSubmit = (e) => {
        window.location.reload();
    }

    render() {
        return (
            <div id='game'>
                <div id='head'>
                Tic Tac Toe
                </div> 
                <div id='board' onClick={(e)=>this.clicked(e)}>
                    <div className='square'data-square="0"></div>
                    <div className='square'data-square="1"></div>
                    <div className='square'data-square="2"></div>
                    <div className='square'data-square="3"></div>
                    <div className='square'data-square="4"></div>
                    <div className='square'data-square="5"></div>
                    <div className='square'data-square="6"></div>
                    <div className='square'data-square="7"></div>
                    <div className='square'data-square="8"></div>
                </div>
                <div id='status'>
                {this.state.winnerLine}
                </div>
                <div>
                    <button id="new" onClick={this.handleSubmit}>
                        New Game
                    </button>    
                </div> 
            </div>
        )
    }
}

export default App;
